import validator from "validator";
import bcrypt from "bcrypt";
import userModel from "../models/userModel.js";
import jwt from "jsonwebtoken";
import transporter from "../config/nodemailer.js";
import { OAuth2Client } from "google-auth-library";

const googleClient = new OAuth2Client(process.env.GOOGLE_CLIENT_ID);

// API to register user
const registerUser = async (req, res) => {
  try {
    const {
      name,
      email,
      password,
      mobile,
      bloodType,
      medicalInsurance,
      gender,
      birthDate,
      allergy,
      drugs = [],
      diseases = [],
      surgeries = [],
      familyHistory = [],
    } = req.body;
    if (
      !name ||
      !password ||
      !email ||
      !mobile ||
      !bloodType ||
      !medicalInsurance ||
      !gender ||
      !birthDate
    ) {
      return res.json({ success: false, message: "Missing Details" });
    }

    // Validating email format
    if (!validator.isEmail(email)) {
      return res.json({ success: false, message: "Enter Valid Email" });
    }

    // Validating strong password
    if (password.length < 8) {
      return res.json({ success: false, message: "Enter Strong Password" });
    }

    // Validating mobile format
    if (!validator.isMobilePhone(mobile, "any")) {
      return res.json({ success: false, message: "Enter Valid Mobile Number" });
    }

    // Validating gender
    if (!["Male", "Female", "Other"].includes(gender)) {
      return res.json({ success: false, message: "Invalid Gender" });
    }

    // Validating birth date
    if (!validator.isDate(birthDate)) {
      return res.json({ success: false, message: "Enter Valid Birth Date" });
    }

    // Check if user already exists
    const existingUser = await userModel.findOne({ email });
    if (existingUser) {
      return res.json({ success: false, message: "Email already registered" });
    }

    // Hashing user password
    const salt = await bcrypt.genSalt(10);
    const hashedPassword = await bcrypt.hash(password, salt);

    // Generate verification OTP
    const otp = String(Math.floor(100000 + Math.random() * 900000));

    const userData = {
      name,
      email,
      mobile,
      password: hashedPassword,
      bloodType,
      medicalInsurance,
      gender,
      birthDate,
      allergy: allergy || {},
      address: { line1: "", line2: "" },
      isAccountVerified: false,
      verifyOtp: otp,
      verifyOtpExpireAt: Date.now() + 24 * 60 * 60 * 1000, // 24 hours expiry
      drugs: drugs.map((drug) => ({
        ...drug,
        prescribedDate: drug.prescribedDate || new Date(),
      })),
      diseases: diseases.map((disease) => ({
        ...disease,
        diagnosedDate: disease.diagnosedDate || new Date(),
      })),
      surgeries: surgeries.map((surgery) => ({  // Add surgeries mapping
        ...surgery,
        date: surgery.date || new Date(),
      })),
      familyHistory: familyHistory.map((item) => ({  // Add familyHistory mapping
        ...item,
        diagnosedDate: item.diagnosedDate || new Date(),
      })),
    };

    const newUser = new userModel(userData);
    await newUser.save();

    // Send OTP email
    const mailOptions = {
      from: process.env.SENDER_EMAIL,
      to: email,
      subject: "Verify Your Kamma-Pharma Account",
      text: `Hello ${name},\n\nThanks for registering.\n\nYour OTP is: ${otp}\n\nPlease verify your account within 24 hours.\n\nâ€“ Kamma-Pharma Team`,
    };
    await transporter.sendMail(mailOptions);

    const token = jwt.sign({ id: newUser._id }, process.env.JWT_SECRET);
    res.json({
      success: true,
      token,
      message: "OTP sent to your email. Please verify your account.",
    });
  } catch (error) {
    console.error("Error registering user:", error);
    res.json({ success: false, message: error.message });
  }
};

// API to login user
const loginUser = async (req, res) => {
  try {
    const { email, password } = req.body;
    const user = await userModel.findOne({ email });

    if (!user) {
      return res.json({ success: false, message: "User Doesn't Exist" });
    }

    const isMatch = await bcrypt.compare(password, user.password);
    if (isMatch) {
      const token = jwt.sign({ id: user._id }, process.env.JWT_SECRET);
      res.json({ success: true, token });
    } else if (!user.isAccountVerified) {
      return res.json({
        success: false,
        message: `Please Check Your Mailbox ${email} to verify your account first before logging in`,
      });
    } else {
      res.json({ success: false, message: "Invalid Credentials" });
    }
  } catch (error) {
    res.json({ success: false, message: error.message });
  }
};

// API to check authentication
const isAuth = async (req, res) => {
  try {
    const user = await userModel.findById(req.user.id).select("-password");
    return res.json({ success: true, user });
  } catch (error) {
    console.log(error.message);
    res.json({ success: false, message: error.message });
  }
};

// Google authentication
const googleAuth = async (req, res) => {
  try {
    const { token } = req.body;

    if (!token) {
      return res.json({ success: false, message: "Google token is required" });
    }

    // Verify the Google token
    const ticket = await googleClient.verifyIdToken({
      idToken: token,
      audience: process.env.GOOGLE_CLIENT_ID,
    });

    const payload = ticket.getPayload();
    const { sub: googleId, email, name, picture } = payload;

    if (!email || !name) {
      return res.json({ success: false, message: "Invalid Google token" });
    }

    // Check if user already exists
    let user = await userModel.findOne({ email });

    if (user) {
      // User exists, update Google info if needed
      if (!user.googleId) {
        user.googleId = googleId;
        user.image = picture || user.image;
        await user.save();
      }

      // Generate JWT token
      const jwtToken = jwt.sign({ id: user._id }, process.env.JWT_SECRET);

      return res.json({
        success: true,
        token: jwtToken,
        message: "Login successful",
        isNewUser: false,
      });
    } else {
      // New user, create account
      const userData = {
        name,
        email,
        googleId,
        image:
          picture ||
          "data:image/png;base64,",
        mobile: "",
        password: "",
        bloodType: "",
        medicalInsurance: "",
        gender: "",
        birthDate: "",
        allergy: {},
        address: { line1: "", line2: "" },
        isAccountVerified: true,
        verifyOtp: "",
        verifyOtpExpireAt: 0,
        isGoogleUser: true,
      };

      const newUser = new userModel(userData);
      await newUser.save();

      const jwtToken = jwt.sign({ id: newUser._id }, process.env.JWT_SECRET);

      return res.json({
        success: true,
        token: jwtToken,
        message: "Account created successfully with Google",
        isNewUser: true,
        needsProfileCompletion: true,
      });
    }
  } catch (error) {
    console.error("Google auth error:", error);
    res.json({
      success: false,
      message: "Google authentication failed. Please try again.",
    });
  }
};

const appleAuth = async (req, res) => {
  try {
    const { identityToken, authorizationCode, user } = req.body;

    if (!identityToken) {
      return res.json({
        success: false,
        message: "Apple identity token is required",
      });
    }

    console.log("Apple auth request received:", {
      hasIdentityToken: !!identityToken,
      hasAuthCode: !!authorizationCode,
      hasUserData: !!user,
    });

    // Verify the identity token with Apple
    let appleUser;
    try {
      appleUser = await appleSignin.verifyIdToken(identityToken, {
        audience: process.env.APPLE_CLIENT_ID,
        issuer: "https://appleid.apple.com",
        // Add these for web applications
        ignoreExpiration: false,
        nonce: undefined, // You can implement nonce validation for extra security
      });
    } catch (verifyError) {
      console.error("Apple token verification failed:", verifyError);
      return res.json({
        success: false,
        message: "Invalid Apple identity token",
      });
    }

    console.log("Apple user verified:", {
      sub: appleUser.sub,
      email: appleUser.email,
      email_verified: appleUser.email_verified,
    });

    // Extract user information
    const appleId = appleUser.sub;
    const email = appleUser.email;
    const emailVerified =
      appleUser.email_verified === "true" || appleUser.email_verified === true;

    // Get name from user object if provided (only available on first sign-in)
    let firstName = "";
    let lastName = "";
    let fullName = "";

    if (user && user.name) {
      firstName = user.name.firstName || "";
      lastName = user.name.lastName || "";
      fullName = `${firstName} ${lastName}`.trim();
    }

    if (!email) {
      return res.json({
        success: false,
        message: "Email is required for account creation",
      });
    }

    // Check if user already exists
    let existingUser = await userModel.findOne({
      $or: [{ email: email }, { appleId: appleId }],
    });

    if (existingUser) {
      // User exists, update Apple info if needed
      let needsUpdate = false;

      if (!existingUser.appleId) {
        existingUser.appleId = appleId;
        needsUpdate = true;
      }

      if (!existingUser.isAppleUser) {
        existingUser.isAppleUser = true;
        needsUpdate = true;
      }

      // Update name if we have it and user doesn't have one
      if (
        fullName &&
        (!existingUser.name || existingUser.name === "Apple User")
      ) {
        existingUser.name = fullName;
        needsUpdate = true;
      }

      if (needsUpdate) {
        await existingUser.save();
      }

      // Generate JWT token
      const jwtToken = jwt.sign(
        { id: existingUser._id },
        process.env.JWT_SECRET
      );

      return res.json({
        success: true,
        token: jwtToken,
        message: "Login successful",
        isNewUser: false,
      });
    } else {
      // New user, create account
      const userData = {
        name: fullName || "Apple User", // Fallback name
        email,
        appleId,
        mobile: "", // Will be set during profile completion
        password: "", // No password for Apple users
        bloodType: "",
        medicalInsurance: "",
        gender: "",
        birthDate: "",
        allergy: {},
        address: { line1: "", line2: "" },
        isAccountVerified: emailVerified, // Apple emails are pre-verified
        verifyOtp: "",
        verifyOtpExpireAt: 0,
        isAppleUser: true, // Flag to identify Apple users
        image:
          "data:image/png;base64,",
      };

      const newUser = new userModel(userData);
      await newUser.save();

      const jwtToken = jwt.sign({ id: newUser._id }, process.env.JWT_SECRET);

      return res.json({
        success: true,
        token: jwtToken,
        message: "Account created successfully with Apple ID",
        isNewUser: true,
        needsProfileCompletion: true,
      });
    }
  } catch (error) {
    console.error("Apple auth error:", error);
    res.json({
      success: false,
      message: "Apple authentication failed. Please try again.",
    });
  }
};

// Send verification OTP
const sendVerifyOtp = async (req, res) => {
  try {
    const { userId } = req.body;
    const user = await userModel.findById(userId);
    if (!user) {
      return res.json({ success: false, message: "User not found" });
    }
    if (user.isAccountVerified) {
      return res.json({ success: false, message: "Account Already Verified" });
    }
    const otp = String(Math.floor(100000 + Math.random() * 900000));
    user.verifyOtp = otp;
    user.verifyOtpExpireAt = Date.now() + 24 * 60 * 60 * 1000; // 24 hours expiry
    await user.save();
    const mailOptions = {
      from: process.env.SENDER_EMAIL,
      to: user.email,
      subject: "Account Verification OTP",
      text: `Hello ${user.name},\n\nYour OTP is: ${otp}\n\nVerify your account within 24 hours.\n\nâ€“ Kamma-Pharma Team`,
    };
    await transporter.sendMail(mailOptions);
    return res.json({ success: true, message: "OTP sent to your email" });
  } catch (error) {
    console.error("Error sending verification OTP:", error);
    res.json({ success: false, message: error.message });
  }
};

// Verify Email
const verifyEmail = async (req, res) => {
  const { userId, otp } = req.body;
  if (!userId || !otp) {
    return res.json({ success: false, message: "Missing Details" });
  }
  try {
    const user = await userModel.findById(userId);
    if (!user) {
      return res.json({ success: false, message: "User not found" });
    }
    if (user.verifyOtp !== otp) {
      return res.json({ success: false, message: "Invalid OTP" });
    }
    if (user.verifyOtpExpireAt < Date.now()) {
      return res.json({ success: false, message: "OTP Expired" });
    }
    user.isAccountVerified = true;
    user.verifyOtp = "";
    user.verifyOtpExpireAt = 0;
    await user.save();
    return res.json({ success: true, message: "Email verified successfully" });
  } catch (error) {
    console.error("Error verifying email:", error);
    res.json({ success: false, message: error.message });
  }
};

// Send reset OTP
const sendResetOtp = async (req, res) => {
  const { email } = req.body;
  if (!email) {
    return res.json({ success: false, message: "Email is required" });
  }
  try {
    const user = await userModel.findOne({ email });
    if (!user) {
      return res.json({ success: false, message: "User not found" });
    }
    const otp = String(Math.floor(100000 + Math.random() * 900000));
    user.resetOtp = otp;
    user.resetOtpExpireAt = Date.now() + 15 * 60 * 1000; // 15 minutes expiry
    await user.save();
    const mailOptions = {
      from: process.env.SENDER_EMAIL,
      to: user.email,
      subject: "Password Reset OTP",
      text: `Hello ${user.name},\n\nYour OTP is: ${otp}\n\nReset your password within 15 minutes.\n\nâ€“ Kamma-Pharma Team`,
    };
    await transporter.sendMail(mailOptions);
    return res.json({
      success: true,
      message: "OTP sent to your email",
      userId: user._id,
    });
  } catch (error) {
    console.error("Error sending reset OTP:", error);
    res.json({ success: false, message: error.message });
  }
};

// Reset user password
const resetPassword = async (req, res) => {
  const { userId, otp, newPassword } = req.body;
  if (!userId || !otp || !newPassword) {
    return res.json({
      success: false,
      message: "User ID, OTP, and new password are required",
    });
  }
  try {
    const user = await userModel.findById(userId);
    if (!user) {
      return res.json({ success: false, message: "User not found" });
    }
    if (user.resetOtp !== otp) {
      return res.json({ success: false, message: "Invalid OTP" });
    }
    if (user.resetOtpExpireAt < Date.now()) {
      return res.json({ success: false, message: "OTP Expired" });
    }
    const hashedPassword = await bcrypt.hash(newPassword, 10);
    user.password = hashedPassword;
    user.resetOtp = "";
    user.resetOtpExpireAt = 0;
    await user.save();
    return res.json({ success: true, message: "Password reset successfully" });
  } catch (error) {
    console.error("Error resetting password:", error);
    res.json({ success: false, message: error.message });
  }
};

